/**
 *  The script only renames the folder of static files generated by `vue-app`
 *  just to prevent the `502 Bad Gate` problem during building
 *
 *  use `shelljs` instead of `mv` to avoid of cross platform problems
 */

var shell = require('shelljs');

shell.echo(' - Renaming the vue-app...');
shell.mv('./dist/app', './dist/vue-app');
